import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

function JSONTree({ data }) {
  const svgRef = useRef();

  useEffect(() => {
    // Clear previous content
    d3.select(svgRef.current).selectAll("*").remove();

    // Dimensions for the tree
    const width = 800;
    const height = 600;

    // Convert JSON to a hierarchical structure
    const root = d3.hierarchy(data, (d) =>
      typeof d === "object" && d !== null ? Object.entries(d) : null
    );

    const treeLayout = d3.tree().size([width - 200, height - 200]);
    treeLayout(root);

    // Create an SVG
    const svg = d3.select(svgRef.current)
      .attr("width", width)
      .attr("height", height)
      .style("border", "1px solid black");

    // Links (edges)
    svg.selectAll(".link")
      .data(root.links())
      .enter()
      .append("line")
      .attr("class", "link")
      .attr("x1", (d) => d.source.x + 100)
      .attr("y1", (d) => d.source.y + 100)
      .attr("x2", (d) => d.target.x + 100)
      .attr("y2", (d) => d.target.y + 100)
      .style("stroke", "#aaa")
      .style("stroke-width", 2);

    // Nodes
    svg.selectAll(".node")
      .data(root.descendants())
      .enter()
      .append("circle")
      .attr("class", "node")
      .attr("cx", (d) => d.x + 100)
      .attr("cy", (d) => d.y + 100)
      .attr("r", 10)
      .style("fill", "#69b3a2");

    // Labels
    svg.selectAll(".label")
      .data(root.descendants())
      .enter()
      .append("text")
      .attr("class", "label")
      .attr("x", (d) => d.x + 110)
      .attr("y", (d) => d.y + 105)
      .text((d) =>
        Array.isArray(d.data) ? `${d.data[0]}: ${d.data[1]}` : d.data
      )
      .style("font-size", "12px")
      .style("fill", "#333");
  }, [data]);

  return <svg ref={svgRef}></svg>;
}

export default JSONTree;
